import React, { useState, useEffect } from 'react';
import { Pie } from 'react-chartjs-2';
import 'chart.js/auto';
import './AdminApp.css';

function AdminApp() {
  const [sessions, setSessions] = useState([]);
  const [sessionDates, setSessionDates] = useState({});
  const [usernames, setUsernames] = useState({});
  const [selectedSession, setSelectedSession] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchSessions();
  }, []);

  // Fetch sessions from the API
  const fetchSessions = async () => {
    setIsLoading(true);
    try {
      const response = await fetch('http://localhost:5000/sessions'); // Update the API endpoint if needed
      if (!response.ok) {
        throw new Error('Failed to fetch sessions');
      }
      const data = await response.json();
      
      console.log('Fetched sessions data:', data); // Log the fetched data to check its structure
      setSessions(data.sessions || []);
      setSessionDates(data.sessionDates || {});
      setUsernames(data.usernames || {}); // Structure: { sessionId: username }
    } catch (error) {
      console.error('Error fetching sessions:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle button click to get analysis for a session
  const handleSessionClick = async (sessionId) => {
    setSelectedSession(sessionId);
    try {
      const response = await fetch(`http://localhost:5000/analyze/${sessionId}`);
      if (!response.ok) {
        throw new Error('Failed to fetch analysis data');
      }
      const data = await response.json();
      setAnalysisData(data);
    } catch (error) {
      console.error('Error fetching analysis data:', error);
    }
  };

  // Handle back button
  const handleBackToSessions = () => {
    setSelectedSession(null);
    setAnalysisData(null);
  };

  // Format the date string
  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  // Render Pie Chart
  const renderPieChart = () => {
    if (!analysisData || !analysisData.overallAnalysis || Object.keys(analysisData.overallAnalysis.emotions).length === 0) {
      return null;
    }

    const emotions = analysisData.overallAnalysis.emotions;
    const emotionLabelsWithEmojis = {
      happy: 'ðŸ˜Š Happy',
      sad: 'ðŸ˜¢ Sad',
      angry: 'ðŸ˜  Angry',
      surprised: 'ðŸ˜® Surprised',
      fearful: 'ðŸ˜¨ Fearful',
      neutral: 'ðŸ˜ Neutral',
      disgust: 'ðŸ¤¢ Disgust',
      fear: 'ðŸ˜¨ Fear',
      surprise: 'ðŸ˜¯ Surprise',
    };

    const data = {
      labels: Object.keys(emotions).map(
        (emotion) => emotionLabelsWithEmojis[emotion] || emotion
      ),
      datasets: [
        {
          data: Object.values(emotions).map((v) => parseFloat(v)),
          backgroundColor: [
            '#FF6384',
            '#36A2EB',
            '#FFCE56',
            '#4BC0C0',
            '#9966FF',
            '#FF9F40',
            '#C9CBCF',
          ],
          borderWidth: 1,
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: 'right',
        },
        title: {
          display: true,
          text: 'Emotion Analysis',
        },
      },
    };

    return (
      <div className="pie-chart-container">
        <Pie data={data} options={options} />
      </div>
    );
  };

  // Render Analysis Table
  const renderTable = () => {
    if (!analysisData || analysisData.imageAnalyses.length === 0) return null;

    return (
      <div className="table-container">
        <table className="analysis-table">
          <thead>
            <tr>
              <th>Webcam Capture</th>
              <th>Game Screenshot</th>
              <th>Analysis</th>
            </tr>
          </thead>
          <tbody>
            {analysisData.imageAnalyses.map((analysis, index) => (
              <tr key={index}>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/webcam_images/${selectedSession}/${analysis.imagePath}`}
                    alt={`Webcam ${index}`}
                    className="webcam-image"
                    loading="lazy"
                  />
                </td>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/screenshots/${selectedSession}/${analysis.imagePath}`}
                    alt={`Game Screenshot ${index}`}
                    className="screenshot-image"
                    loading="lazy"
                  />
                </td>
                <td>
                  <h4>Emotion Analysis:</h4>
                  {Object.entries(analysis.emotions).map(([emotion, value]) => (
                    <div key={emotion}>
                      <span>{emotion}:</span>
                      <span>{parseFloat(value).toFixed(2)}%</span>
                    </div>
                  ))}
                  <strong>Dominant Emotion: </strong>
                  <span>{analysis.dominantEmotion}</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  // Render Session List
  const renderSessionList = () => (
    <div style={{ width: '100%' }}>
      <h3>Session List</h3>
      {isLoading ? (
        <p>Loading sessions...</p>
      ) : (
        <table className="session-table">
          <thead>
            <tr>
              <th>Username</th>
              <th>Session ID</th>
              <th>Date & Time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {sessions.map((session, index) => (
              <React.Fragment key={index}>
                <tr>
                  <td>{usernames[session] || 'Unknown User'}</td>
                  <td>{session}</td>
                  <td>{formatDate(sessionDates[session])}</td>
                  <td>
                    <button onClick={() => handleSessionClick(session)}>
                      Get Analysis
                    </button>
                  </td>
                </tr>
              </React.Fragment>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      {selectedSession ? (
        <div>
          <h3>Analysis for Session: {selectedSession}</h3>
          {renderPieChart()}
          {renderTable()}
          <button onClick={handleBackToSessions}>Back to Sessions</button>
        </div>
      ) : (
        renderSessionList()
      )}
    </div>
  );
}

export default AdminApp;
