  return (
    <div className="admin-app-container">
      <h2>Admin Dashboard</h2>
      {isLoading ? (
        <p>Loading...</p>
      ) : (
        <div className="table-container">
          <table className="sessions-table">
            <thead>
              <tr>
                <th>Username</th>
                <th>Session ID</th>
                <th>Created Date</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {sessions.length > 0 ? (
                sessions.map((session) => (
                  <tr key={session}>
                    <td>{usernames[session] || 'Unknown User'}</td>
                    <td>{session}</td>
                    <td>{formatDate(sessionDates[session])}</td>
                    <td>
                      <button
                        onClick={() => handleSessionClick(session)}
                        className="get-analysis-btn"
                      >
                        Get Analysis
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4" className="no-sessions">
                    No sessions available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
